import React, { useEffect, useState } from "react";

const Comment = (props) => {
    const [comment, setComment] = useState([]);
    useEffect(() => {
      setComment(props.data);
    }, [setComment]);
    return (
        <div>
            <h1>{comment}</h1>
        </div>
    )
}

export default Comment
