import Joi from 'joi-browser';

const postSchema = {
    title: Joi.string().required().min(1),
    text: Joi.string().required().min(1),
}

export const validateInputText = (post) =>{
    return Joi.validate(post, postSchema);
}

export const validateInputFile = (imageFile) => {
    if(!imageFile)
        return "File input must not be empty!";
    if(!imageFile.name.match(/\.(jpg|jpeg|png)$/))
        return "Invalid image type! Please select a valid image type! (jpg|jpeg|png)"
    return "Success!";
    

}
