import React, { useEffect, useState } from "react";
import Comment from "./Comment";
import { sendComment } from "../Services/PostService";
import { Container } from "@material-ui/core";
import { Form, Button, Row, Col } from "react-bootstrap";

const Comments = (props) => {
  const [comments, setComments] = useState([]);
  const [userComment, setUserComment] = useState();
  useEffect(() => {
    console.log("Comments: ", props);
    setComments(props.Comments);
  }, [setComments]);

  const handleSubmit = async (event) => {
    const commentToSend = { text: userComment, postId: props.postId };
    console.log("commentToSend from <Comments>:",commentToSend);
    const response = sendComment(commentToSend);
    // console.log("Response of Comment from Comments:", response);
    event.preventDefault();
  };

  const handleChange = (event) => {
    setUserComment({
      ...userComment,
      [event.target.name]: event.target.value,
    });
  };

  return (
    <div>
      <Container className="mt-1 p-1">
        {comments && comments.length > 0 ? (
          comments.map((comment) => (
            <React.Fragment>
              <Comment Comment={comment} />
            </React.Fragment>
          ))
        ) : (
          <h1>Loading Data...</h1>
        )}
      </Container>
      <Form onSubmit={handleSubmit}>
        <Row>
          <Col xs={5}>
            <Form.Control name="text" type="text" onChange={handleChange} />
          </Col>
          <Col xs={2}>
            <Button
              className="ml-1 form-control-sm"
              variant="primary"
              type="submit"
            >
              Comment
            </Button>
          </Col>
        </Row>
      </Form>
    </div>
  );
};

export default Comments;
