import React from "react";
import {Card, Button} from "react-bootstrap"


const Post = (post) => {
  const routeChange = () => {
    let path = ""
  }
  const {data} = post;
  return (
    <Card className="border-shadow d-inline-block mx-5" style={{ width: '18rem' }}>
  <Card.Img variant="top"  style={{ height:'200px' ,objectFit: 'contain'}} src={data.img} />
  <Card.Body>
    <Card.Title>{data.title}</Card.Title>
    <Card.Text>
      {data.text}
    </Card.Text>
    {/* <Button variant="primary">Go somewhere</Button> */}
  </Card.Body>
</Card>
  );
};

export default Post;
